import React, { useEffect, useState } from "react";

function Dashboard() {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/intern")
      .then((res) => res.json())
      .then(setData);
  }, []);

  if (!data) return <div>Loading...</div>;

  return (
    <div
      style={{
        maxWidth: 500,
        margin: "50px auto",
        padding: 20,
        border: "1px solid #ccc",
        borderRadius: 8,
      }}
    >
      <h2>Intern Dashboard</h2>
      <p>
        <strong>Name:</strong> {data.name}
      </p>
      <p>
        <strong>Referral Code:</strong> {data.referralCode}
      </p>
      <p>
        <strong>Total Donations Raised:</strong> ${data.donations}
      </p>
      <h3>Rewards/Unlockables</h3>
      <ul>
        <li>Reward 1 (static)</li>
        <li>Reward 2 (static)</li>
      </ul>
    </div>
  );
}

export default Dashboard;
